#include <iostream>
#include "bst.h"

int main() {
    BST tree;

    // Insert values
    int values[] = {50, 30, 70, 20, 40, 60, 80};
    for (int val : values) {
        tree.insert(val);
    }

    std::cout << "Inorder: ";
    tree.inorder();

    std::cout << "Height: " << tree.height() << std::endl;
    std::cout << "Count: " << tree.nodeCount() << std::endl;

    // Search
    std::cout << "Search 40: " << (tree.search(40) ? "Found" : "Not found") << std::endl;

    // Remove
    tree.remove(30);
    std::cout << "After removing 30: ";
    tree.inorder();

    return 0;
}
